import {PRODUCT_LIST_FAIL, PRODUCT_LIST_REQ, PRODUCT_LIST_SUCCESS} from "../../constans/productConst";
import axios from "axios";
global.Buffer

const API_PRODUCTS = '/api/v1/product'

const token = Buffer.from('igorzn:password').toString('base64')
const headers = {
    Authorization: `Basic ${token}`
}

export const listProducts = () => {
    return (dispatch) => {
        try {
            dispatch({type: PRODUCT_LIST_REQ})
            const {data} = axios.get(API_PRODUCTS, {}, {
                headers
            })

            dispatch({
                type: PRODUCT_LIST_SUCCESS,
                payload: data
            })

        } catch (e) {
            console.log('listProducts>>>', e)
            dispatch({
                type: PRODUCT_LIST_FAIL,
                error: e.response && e.response.data.message
                    ? e.response.data.message
                    : e.message
            })
        }
    }
}
